function auto(){
    // Определяем массив
    var arr = [18, 16, 14, 12, 10, 8, 6, 15, 13, 11, 9, 7];
    // Получаем случайный ключ массива
    var randKey = Math.floor(Math.random() * arr.length);
    let rand = arr[randKey];
    console.log(rand);
    //skleika
    const autoBtnVar = `btn${rand}`;
    const autoImgVar = `i${rand}`; 
    const autoBtn = document.querySelector(`.${autoBtnVar}`);
    const autoImg = document.querySelector(`.${autoImgVar}`);
    console.log(autoBtn);
    console.log(autoImg);
    if (!autoBtn.classList.contains('_active-btn')) {
        autoOn.innerHTML = `<span>${(date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds())}: Отопление в доме ${rand} было </span><span class="hot">включено</span>`;
        autoBtn.classList.add('_active-btn');
        autoImg.classList.add('_active');
        jurnalText.insertAdjacentElement(
            'afterbegin',
            autoOn
        );
        autoOn = document.createElement('div');
    } else {
        autoOff.innerHTML = `<span>${(date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds())}: Отопление в доме ${rand} было </span><span class="cold">выключено</span>`;
        autoBtn.classList.remove('_active-btn');
        autoImg.classList.remove('_active');
        jurnalText.insertAdjacentElement(
            'afterbegin',
            autoOff
        );
        autoOff = document.createElement('div');
    };

    console.log('the end');
}

